import React, { useEffect, useState } from 'react';
import Css from './components/App.css'
const App = () => {

  const [city, setCity] = useState(null);
  const [Search, setSearch] = useState("Varanasi");

  useEffect(() => {
    const fetchApi = async () => {
      const url = `http://api.openweathermap.org/data/2.5/weather?q=${Search}&appid=3558afab6dedafab685ac52205d05221`
      // dont forget to add http before the api url
      const res = await fetch(url)
      // console.log(res)
      const resJSON = await res.json();
      console.log(resJSON);
      setCity(resJSON.main)

    }
    fetchApi();
  }, [setSearch])

  return (
    <>
      <h1 className="h11">Weather App</h1>
      <div className="card">
        <div className="box">
          <div className="inputData">
            <input type="search" className="inputField"
              placeholder="Search city"
              onChange={(event) => {
                setSearch(event.target.value)
              }} />
          </div>
        // </div>
        {!city ? (
          <p> No data found</p>
        ) : (

          <div className="info">
            <h2 className="location">
              {Search}</h2>
            <h1 className="temp">{city.temp}</h1>
            <h3 className="tempmin_max">Min : 10deg Cel <p>Max : 10Deg Cel</p> </h3>

          </div>


        )
        }
      </div>


    </>
  )
}
export default App
